using AdventOfCode2017.Interfaces;
using System;
using System.Collections.Generic;
using System.Text;

namespace AdventOfCode2017.Challenges
{
    public class Day19 : IChallengeDay
    {
        public string Part01(string input)
        {
            int row = 0, col = 0, rowInc = 1, colInc = 0;
            var letters = new StringBuilder();
            var pipeChars = new HashSet<char>() { '|', '-', '+' };
            char empty = ' ';
            bool endOfLine = false;
            string[] lines = input.Split(new char[] { '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries);

            // determine first entry
            for (int i = 0; i < lines[0].Length; i++) { if (lines[0][i] == '|') { col = i; } }

            // follow the maze
            while (!endOfLine)
            {
                char currentSegment = lines[row][col];
                if (currentSegment == empty) { endOfLine = true; break; }
                if (!pipeChars.Contains(currentSegment))
                {
                    letters.Append(currentSegment);
                }
                if (currentSegment == '+')
                {
                    // change direction
                    int lastRowInc = rowInc, lastColInc = colInc;
                    if (lastRowInc != 1 && row > 0 && lines[row - 1][col] != empty) { rowInc = -1; colInc = 0; }
                    if (lastRowInc != -1 && row < (lines.Length-1) && lines[row + 1][col] != empty) { rowInc = 1; colInc = 0; }
                    if (lastColInc != 1 && col > 0 && lines[row][col - 1] != empty) { rowInc = 0; colInc = -1; }
                    if (lastColInc != -1 && col < (lines[row].Length-1) && lines[row][col + 1] != empty) { rowInc = 0; colInc = 1; }
                }

                row += rowInc;
                col += colInc;
            }

            return letters.ToString();
        }

        public string Part02(string input)
        {
            int row = 0, col = 0, rowInc = 1, colInc = 0, steps=0;
            var letters = new StringBuilder();
            var pipeChars = new HashSet<char>() { '|', '-', '+' };
            char empty = ' ';
            bool endOfLine = false;
            string[] lines = input.Split(new char[] { '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries);

            // determine first entry
            for (int i = 0; i < lines[0].Length; i++) { if (lines[0][i] == '|') { col = i; } }

            // follow the maze
            while (!endOfLine)
            {
                char currentSegment = lines[row][col];
                if (currentSegment == empty) { endOfLine = true; break; }
                if (!pipeChars.Contains(currentSegment))
                {
                    letters.Append(currentSegment);
                }
                if (currentSegment == '+')
                {
                    // change direction
                    int lastRowInc = rowInc, lastColInc = colInc;
                    if (lastRowInc != 1 && row > 0 && lines[row - 1][col] != empty) { rowInc = -1; colInc = 0; }
                    if (lastRowInc != -1 && row < (lines.Length - 1) && lines[row + 1][col] != empty) { rowInc = 1; colInc = 0; }
                    if (lastColInc != 1 && col > 0 && lines[row][col - 1] != empty) { rowInc = 0; colInc = -1; }
                    if (lastColInc != -1 && col < (lines[row].Length - 1) && lines[row][col + 1] != empty) { rowInc = 0; colInc = 1; }
                }

                row += rowInc;
                col += colInc;
                steps++;
            }

            return steps.ToString();
        }

